import React, { useState } from 'react';
import './TorchCursor.css';

const TorchCursor = () => {
  const [position, setPosition] = useState({ x: -100, y: -100 });

  const handleMouseMove = (e) => {
    setPosition({ x: e.clientX, y: e.clientY });
  };

  return (
    <div className="torch-cursor" onMouseMove={handleMouseMove}>
      <div className="torch" style={{ left: `${position.x}px`, top: `${position.y}px` }}></div>
    </div>
  );
};

export default TorchCursor;
