import React, { useState } from 'react';
import './Main.css';
import box from '../../Assets/letter_box.png';

const Main = ({ showAnswer, onShowAnswer }) => {
  const [div2Visible, setDiv2Visible] = useState(false);
  const [noButtonOpacity, setNoButtonOpacity] = useState(1);

  const handleYes1Click = () => {
    setDiv2Visible(true);
  };

  const handleNoHover = () => {
    setNoButtonOpacity(0);
  };

  const handleNoLeave = () => {
    setNoButtonOpacity(1);
  };

  return (
    <div className='main'>
      <div className='content'>
        <h1>this is a secret message, only Rwihiba zwiiina should read it</h1>
        <img src={box} alt='box' id='box' />
        <div id='div1'>
          <h2>are you Rwihiba wrida ?</h2>
          <div>
            <button id='yes1' onClick={handleYes1Click}>yes</button>
            <button
              id='no'
              onMouseEnter={handleNoHover}
              onMouseLeave={handleNoLeave}
              style={{ opacity: noButtonOpacity, transition: 'opacity 0.5s ease-in-out' }}>
              no
            </button>
          </div>
        </div>
        <div id='div2' style={{ display: div2Visible ? 'block' : 'none' }}>
          <h2>the message is locked would u like to search for the key to open you message ?</h2>
          <div>
            <button id='yes2'>yes</button>
            <button
              id='no'
              onMouseEnter={handleNoHover}
              onMouseLeave={handleNoLeave}
              style={{ opacity: noButtonOpacity, transition: 'opacity 1s ease-in-out' }}>
              no
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Main;
