  const handleClick = () => {
    setShowTorchEffect(false);
  };

  return (
    <div>
      {showTorchEffect && <TorchEffect2 />}
      <Heart showTorchEffect={showTorchEffect} handleClick={handleClick} />
    </div>
  )
}

