import React, { useState } from 'react';
import './Main.css';
import me from '../../Assets/me.png';
import mmssg1 from '../../Assets/mssg1.png';
import mmssg2 from '../../Assets/mssg2.png';
import mmssg3 from '../../Assets/mssg3.png';

const Main = () => {
  const [clickCount, setClickCount] = useState(0);

  const messages = [mmssg1, mmssg2, mmssg3];

  const showNextMessage = () => {
    setClickCount((prevCount) => prevCount + 1);
  };

  return (
    <div className='main'>
      <div className='messages'>
        {messages.slice(0, clickCount).map((message, index) => (
          <img
            key={index}
            src={message}
            alt={`message${index + 1}`}
            className={`message message-${index + 1}`}
          />
        ))}
        <img src={me} alt='me' id='me' />
      </div>
      {clickCount < messages.length && <button onClick={showNextMessage} className='nextmssg'>Next Mssg...</button>}
    </div>
  );
};

export default Main;
