import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import door from '../../Assets/door.png';
import wrongkey from '../../Assets/wrongkey.png';
import './Key.css';

const Key = () => {
  const [showWrongKey, setShowWrongKey] = useState(false);

  const handleDoorClick = () => {
    setShowWrongKey(true);
  };

  return (
    <div className='heart-container'>
      <div className='row'>
        <Door onClick={handleDoorClick} />
        <Door onClick={handleDoorClick} />
        <Door onClick={handleDoorClick} />
      </div>
      <div className='row'>
        <Door onClick={handleDoorClick} />
        <Door onClick={handleDoorClick} id='5thdoor' />
        <Door onClick={handleDoorClick} />
      </div>
      <div className='row'>
        <Door onClick={handleDoorClick} />
        <Door onClick={handleDoorClick} />
        <Door onClick={handleDoorClick} />
      </div>
      {showWrongKey && <DoorWithWrongKey />}
    </div>
  );
};

const Door = ({ onClick }) => {
  const [opacity, setOpacity] = useState(1);

  const handleClick = () => {
    setOpacity(0);
    onClick();
  };

  return (
    <div className='door-container' style={{ opacity: opacity, transition: 'opacity 0.2s' }} onClick={handleClick}>
      <img src={door} alt='door' className='door' />
    </div>
  );
};

const DoorWithWrongKey = () => {
  return (
    <div className='wrong-key-centered'>
      <Link to="/Final_Page" id='keylink'>
        <img src={wrongkey} alt='wrong key' className='wrong-key' style={{ zIndex: 2 }} />
      </Link>
    </div>
  );
};

export default Key;
