import React, { useState } from 'react';
import './Main.css'
import me from '../../Assets/me.png'
import mmssg1 from '../../Assets/mssg1.png'
import mmssg2 from '../../Assets/mssg2.png'
import mmssg3 from '../../Assets/mssg3.png'

const Main = () => {
  const [currentMessage, setCurrentMessage] = useState(null);

  const messages = [mmssg1, mmssg2, mmssg3];

  const showNextMessage = () => {
    setCurrentMessage((prevMessage) => {
      const nextIndex = prevMessage === null ? 0 : (prevMessage + 1) % messages.length;
      return nextIndex;
    });
  };

  return (
    <div className='main'>
      <div className='messages'>
        {messages.map((message, index) => (
          <img
            key={index}
            src={message}
            alt={`message${index + 1}`}
            className={`message ${currentMessage === index ? 'show' : ''}`}
          />
        ))}
        <img src={me} alt='me' id='me' />
      </div>
      <button onClick={showNextMessage} className='nextmssg'>Next Mssg...</button>
    </div>
  );
};

export default Main
